
if (innerWidth < 767) {
	let tenses = document.querySelectorAll('.grid__tense');
	tenses.forEach(tense => {
		if (!tense.classList.contains('additional-padding')) {
			tense.classList.add('additional-padding')
		}
	});
};

import irregularList from "../files/irregular_list.json"
let table = document.querySelector('.table')
console.log(table);
console.log(irregularList.verbs);
//значения location.pathname, при которых будет запускаться скрипт
let pagesWithScript = ["/irregular-verbs.html"];
if (pagesWithScript.indexOf(location.pathname) != -1) {
	// Json irregular table
	console.log(pagesWithScript);
	function buildTable(data) {
		for (let i = 0; i < data.length; i++) {
			let row = `<tr>
									<td>${data[i].base}</td>
									<td>${data[i].pastSimple}</td>
									<td>${data[i].pastParticiple}</td>
								</tr>`
			console.log(row);
			table.innerHTML += row
		}
	}
	console.log(irregularList.verbs);
	buildTable(irregularList.verbs)

	// Search
	window.onload = () => {
		const input = document.querySelector('#search');
		input.oninput = function () {
			let value = this.value.trim();
			let list = document.querySelectorAll('tbody tr')
			if (value) {
				list.forEach(elem => {
					if (elem.innerText.search(value) == -1) {
						elem.classList.add('hide');
					}
				});
			} else {
				list.forEach(elem => {
					elem.classList.remove('hide')
				})
			}
		}
	}
}
